# Don't use Markup, just return the raw strings
def highlight_term(text, term):
    pattern = re.compile(re.escape(term), re.IGNORECASE)
    return pattern.sub(f'<mark>{term}</mark>', text)



@app.route('/search')
def search():
    search_term = request.args.get('search', '').lower().strip()
    matching_items = []


    for item in items_list:
        item_copy = item.copy()  # Work on a copy to preserve original data
        matched = False

        # Check for match in name and highlight if found
        if search_term in item['name'].lower():
            item_copy['name'] = highlight_term(item['name'], search_term)
            matched = True

        # Check for match in list_data (ingredients) and highlight if found
        highlighted_ingredients = []
        ingredient_match = False
        for ingredient in item['list_data']:
            if search_term in ingredient.lower():
                highlighted_ingredients.append(highlight_term(ingredient, search_term))
                ingredient_match = True
                matched = True
            else:
                highlighted_ingredients.append(ingredient)

        # Update item_copy with possibly highlighted ingredients
        item_copy['list_data'] = highlighted_ingredients
        if ingredient_match:
            # Append ingredient match to item name for clarity
            item_copy['name'] += f" - {', '.join(highlighted_ingredients)}"

        # Check if the search term is a digit and if so, compare against calories
        if search_term.isdigit():
            if str(search_term) in str(item['calories']):
                item_copy['calories'] = highlight_term(str(item['calories']), search_term)
                matched = True

        if matched:
            matching_items.append(item_copy)

    item_count = len(matching_items)
    return render_template(
        'show.html',
        search_term=search_term,
        items=matching_items,
        item_count=item_count
    )





@app.route('/view/<int:item_id>')
def view_item(item_id):
    # Find the item by ID
    item = next((item for item in items_list if item['id'] == item_id), None)
    if item:
        # Render a template to view the item, passing the item as a variable
        return render_template('view_item.html', item=item)
    else:
        return 'Item not found', 404



@app.route('/item/<int:item_id>')
def item_detail(item_id):
    # Find the item with the given ID
    item = next((item for item in items_list if item['id'] == item_id), None)
    if item is not None:
        return render_template('item_detail.html', item=item)
    else:
        return 'Item not found', 404


@app.route('/add', methods=['GET', 'POST'])
def add_item():
    if request.method == 'POST':
        # Extract form data
        name = request.form.get('name', '').strip()
        media_link = request.form.get('media_link', '').strip()
        description = request.form.get('description', '').strip()
        calories = request.form.get('calories', '').strip()
        # Assuming list_data comes as a string of comma-separated values
        list_data_str = request.form.get('list_data', '').strip()
        fat = request.form.get('fat', '0').strip()
        carbohydrates = request.form.get('carbohydrates', '0').strip()
        protein = request.form.get('protein', '0').strip()
        fiber = request.form.get('fiber', '0').strip()
        sugar = request.form.get('sugar', '0').strip()
        sodium = request.form.get('sodium', '0').strip()

         # Validation for empty or whitespace inputs
        if not name:
            return jsonify({'error': 'Name cannot be empty or consist of just whitespace.'}), 400
        if not description:
            return jsonify({'error': 'Description cannot be empty or consist of just whitespace.'}), 400


        pattern = r'^\s*[^,]+(?:\s*,\s*[^,]+)*\s*$'

        # Check if the list_data_str matches the pattern
        if not re.match(pattern, list_data_str):
            # If the input format is incorrect, return an error
            return jsonify({'error': 'Invalid format for list data. Input must separate items by commas, e.g., [Item1, Item2, Item3].'}), 400

        # Basic validation (Checking if required fields are not empty)
        if not name or not description:
            # This should ideally redirect back with an error message
            # For simplicity, we're just returning a JSON response here
            return jsonify({'error': 'Name and description are required.'}), 400

        numeric_fields = {
            'calories': calories,
            'fat': fat,
            'carbohydrates': carbohydrates,
            'protein': protein,
            'fiber': fiber,
            'sugar': sugar,
            'sodium': sodium
        }

        # Validate each numeric field
        for field, value in numeric_fields.items():
            if any(c.lower() == 'e' for c in value):  # Check for 'e' to prevent exponential input
                return jsonify({'error': f'{field.capitalize()} must be a valid non-exponential number.'}), 400
            try:
                numeric_fields[field] = float(value)
            except ValueError:
                return jsonify({'error': f'{field.capitalize()} must be a valid number.'}), 400
            
        
        # Convert numeric values safely
        try:
            calories = float(calories)
            fat = float(fat)
            carbohydrates = float(carbohydrates)
            protein = float(protein)
            fiber = float(fiber)
            sugar = float(sugar)
            sodium = float(sodium)
        except ValueError:
            return jsonify({'error': 'Numeric fields must contain valid numbers.'}), 400

        # Process list_data_str into a list
        list_data = list_data_str.split(',') if list_data_str else []

        # Creating new item
        new_item = {
            "id": len(items_list) + 1,  # Simple ID generation based on list length
            "name": name,
            "media_link": media_link,
            "description": description,
            "calories": calories,
            "list_data": list_data,
            "fat": fat,
            "carbohydrates": carbohydrates,
            "protein": protein,
            "fiber": fiber,
            "sugar": sugar,
            "sodium": sodium
        }

        # Add the new item to items_list
        items_list.append(new_item)

        # Assuming success, redirect to home page or return success message
        # In a real application, consider redirecting to a confirmation page or displaying a success message
        return jsonify({'message': 'New item successfully created.', 'itemId': len(items_list)})

    # GET request or initial page load
    return render_template('add_item.html')


@app.route('/edit/<int:item_id>', methods=['GET', 'POST'])
def edit_item(item_id):
    item = next((item for item in items_list if item['id'] == item_id), None)
    if not item:
        return 'Item not found', 404

    if request.method == 'POST':
        # Extract form data and strip whitespace
        name = request.form.get('name', '').strip()
        media_link = request.form.get('media_link', '').strip()
        description = request.form.get('description', '').strip()
        calories = request.form.get('calories', '').strip()
        list_data_str = request.form.get('list_data', '').strip()
        fat = request.form.get('fat', '0').strip()
        carbohydrates = request.form.get('carbohydrates', '0').strip()
        protein = request.form.get('protein', '0').strip()
        fiber = request.form.get('fiber', '0').strip()
        sugar = request.form.get('sugar', '0').strip()
        sodium = request.form.get('sodium', '0').strip()

        # Validation for empty or whitespace inputs
        if not name:
            return jsonify({'error': 'Name cannot be empty or consist of just whitespace.'}), 400
        if not description:
            return jsonify({'error': 'Description cannot be empty or consist of just whitespace.'}), 400

        numeric_fields = {
            'calories': calories,
            'fat': fat,
            'carbohydrates': carbohydrates,
            'protein': protein,
            'fiber': fiber,
            'sugar': sugar,
            'sodium': sodium
        }

        # Validate each numeric field
        for field, value in numeric_fields.items():
            if any(c.lower() == 'e' for c in value):  # Check for 'e' to prevent exponential input
                return jsonify({'error': f'{field.capitalize()} must be a valid non-exponential number.'}), 400
            try:
                numeric_fields[field] = float(value)
            except ValueError:
                return jsonify({'error': f'{field.capitalize()} must be a valid number.'}), 400

        # Process list_data_str into a list if it's not empty
        list_data = list_data_str.split(',') if list_data_str else []

        # Update item with new values
        item['name'] = name
        item['media_link'] = media_link
        item['description'] = description
        item['calories'] = numeric_fields['calories']
        item['list_data'] = list_data
        item['fat'] = numeric_fields['fat']
        item['carbohydrates'] = numeric_fields['carbohydrates']
        item['protein'] = numeric_fields['protein']
        item['fiber'] = numeric_fields['fiber']
        item['sugar'] = numeric_fields['sugar']
        item['sodium'] = numeric_fields['sodium']

        # Assuming success, redirect to view page or return success message
        return jsonify({'message': 'Item updated successfully.', 'itemId': item_id})

    return render_template('edit_item.html', item=item)


